/*Ejercicio_1:*/
/*Crear un índice no único en la columna de claves ajenas (DEPT_NO) en la tabla 
EMPLE.*/

DROP INDEX daw25_emp_deptno;

CREATE INDEX daw25_emp_deptno 
ON emple(dept_no);
/*Index DAW25_EMP_DEPTNO creado.*/

SELECT i.index_name, c.column_name, c.column_position, i.uniqueness
FROM user_indexes i, user_ind_columns c
WHERE i.index_name = c.index_name -- join
AND UPPER(c.table_name) = 'EMPLE'; -- tabla
/*
INDEX_NAME                                                                                                                       COLUMN_NAME                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    