                                                                                                                                                1 NONUNIQUE
*/

SELECT a.constraint_name, a.constraint_type, a.index_name, b.column_name
FROM user_constraints a, user_cons_columns b
WHERE a.constraint_name = b.constraint_name -- join
AND UPPER(a.table_name) = 'EMPLE';
